"use client";
import { createRoot } from "react-dom/client";
import "./App.css";
import React, { useRef, useState, useEffect } from "react";
import {
  APIProvider,
  Marker,
  Map,
} from "@vis.gl/react-google-maps";
import Form from "./components/Form.js";

export default function App() {
  const [position, setPosition] = useState({ lat: 51, lng: 10 });
  const [data, setData] = useState({
    car: "",
    walk: "",
    bus: "",
    coX: "",
    coY: "",
    coX1: "",
    coY1: "",
    coX2: "",
    coY2: "",
  });

  const [googleMapsLoaded, setGoogleMapsLoaded] = useState(false);

  // Dynamically load the Google Maps API
  useEffect(() => {
    const script = document.createElement("script");
    script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_API_KEY}&libraries=places,routes`;
    script.async = true;
    script.defer = true;
    script.onload = () => {
      setGoogleMapsLoaded(true);
    };
    document.head.appendChild(script);
  }, []);

  const [map, setMap] = useState(null);
  const [dResponse, setDResponse] = useState(null);
  const [list, setList] = useState([]);

  const [bestX, setBestX] = useState(0);
  const [bestY, setBestY] = useState(0);
  const key = process.env.REACT_APP_API_KEY;

  useEffect(() => {
    if (bestX !== 0 && bestY !== 0) {
      console.log("You should live at:", { lat: bestX, lng: bestY });
      setPosition({ lat: bestX, lng: bestY });
    }
  }, [bestX, bestY]);

  useEffect(() => {
    startCalcs(data);
  }, [data]);

  const Place = (xCo, yCo, freq) => ({ xCo, yCo, freq });

  return (
    <APIProvider apiKey={key}>
      <div className="App">
        <header className="App-header">
          <h3>
            <i className="fas fa-home"></i> NuCasa{" "}
            <i className="fas fa-map-marker-alt"></i>
          </h3>
          <i className="small-text">
            The perfect app for quick location triangulation!
          </i>
        </header>
      </div>
      <div className="split left-panel">
        <Form data={data} setData={setData} />
      </div>
      <div className="split right-panel" style={{ height: "95vh" }}>
        <Map zoom={9} center={position} onLoad={(map) => setMap(map)}>
          {dResponse && <useDirectionsRenderer directions={dResponse} />}
          <Marker position={position} />
        </Map>
      </div>
    </APIProvider>
  );

  async function startCalcs(data) {
    const places = [
      Place(data.coX, data.coY, data.freq),
      Place(data.coX1, data.coY1, data.freq1),
      Place(data.coX2, data.coY2, data.freq2),
      Place(data.coX3, data.coY3, data.freq3),
      Place(data.coX4, data.coY4, data.freq4),
    ];

    const validPlaces = places.filter(
      (place) => place.xCo !== "" && place.yCo !== "" && place.freq !== ""
    );

    if (validPlaces.length === 0) return;

    setList(validPlaces);

    const minMaxCoords = validPlaces.reduce(
      (acc, place) => {
        acc.minX = Math.min(acc.minX, place.xCo);
        acc.maxX = Math.max(acc.maxX, place.xCo);
        acc.minY = Math.min(acc.minY, place.yCo);
        acc.maxY = Math.max(acc.maxY, place.yCo);
        return acc;
      },
      { minX: 90, maxX: -90, minY: 90, maxY: -90 }
    );

    await findBestHome(
      minMaxCoords.minX,
      minMaxCoords.maxX,
      minMaxCoords.minY,
      minMaxCoords.maxY
    );
  }

  async function findBestHome(minX, maxX, minY, maxY) {
    let bestScore = Infinity;
    let firstBestX = 0;
    let firstBestY = 0;
    const xDiff = (maxX - minX) / 5;
    const yDiff = (maxY - minY) / 5;

    for (let it = 0; it < 5; it++) {
      for (let it1 = 0; it1 < 5; it1++) {
        const testX = minX + it * xDiff;
        const testY = minY + it1 * yDiff;
        const testVal = await calculateStrength(testX, testY);

        if (testVal < bestScore) {
          bestScore = testVal;
          firstBestX = testX;
          firstBestY = testY;
        }
      }
    }

    setBestX(firstBestX);
    setBestY(firstBestY);
  }

  async function calculateStrength(xCo, yCo) {
    let totalScore = 0;

    for (const place of list) {
      const dScore = await findRoute(xCo, yCo, place.xCo, place.yCo, "DRIVING");
      const bScore = await findRoute(xCo, yCo, place.xCo, place.yCo, "BICYCLING");
      const wScore = await findRoute(xCo, yCo, place.xCo, place.yCo, "WALKING");

      const bestScore = Math.min(dScore, bScore, wScore);
      totalScore += bestScore * place.freq;
    }

    return totalScore;
  }

  async function findRoute(xCo, yCo, xCo1, yCo1, method) {
    const { DirectionsService } = await google.maps.importLibrary("routes");
    const dService = new DirectionsService();

    const origin = new google.maps.LatLng(xCo, yCo);
    const destination = new google.maps.LatLng(xCo1, yCo1);

    const result = await dService.route({
      origin,
      destination,
      travelMode: method,
    });

    return parseTime(result.routes[0].legs[0].duration.text);
  }

  function parseTime(inputString) {
    const regex = /(\d+) hours? (\d+) mins?/;
    const match = inputString.match(regex);
    if (match) {
      const hours = parseInt(match[1], 10);
      const mins = parseInt(match[2], 10);
      return hours + mins / 60;
    }

    const minOnlyRegex = /(\d+) mins?/;
    const minMatch = inputString.match(minOnlyRegex);
    if (minMatch) {
      const mins = parseInt(minMatch[1], 10);
      return mins / 60;
    }

    return 0;
  }
}

window.App = App;
